Extensions.ExtractScript:
param(
    [xml] $Document
);

foreach( $file in $Document.unattend.Extensions.File ) {
    $path = [System.Environment]::ExpandEnvironmentVariables( $file.GetAttribute( 'path' ) );
    mkdir -Path( $path | Split-Path -Parent ) -ErrorAction 'SilentlyContinue';
    $encoding = switch( [System.IO.Path]::GetExtension( $path ) ) {
        { $_ -in '.ps1', '.xml' } { [System.Text.Encoding]::UTF8; }
        { $_ -in '.reg', '.vbs', '.js' } { [System.Text.UnicodeEncoding]::new( $false, $true ); }
        default { [System.Text.Encoding]::Default; }
    };
    $bytes = $encoding.GetPreamble() + $encoding.GetBytes( $file.InnerText.Trim() );
    [System.IO.File]::WriteAllBytes( $path, $bytes );
}

Extensions.File:
$scripts = @(
	{
		$proc = $null
		$sw = [Diagnostics.Stopwatch]::StartNew()
		do {
			try {
				$proc = Get-Process -Name "sysprep" -ErrorAction SilentlyContinue
			} catch { }
			if ($proc) { break }
			Start-Sleep -Milliseconds 500
		} while ($sw.Elapsed.TotalSeconds -lt 180)		

		if (-not $proc) { return }

		try {
			Stop-Process -Id $proc.Id -Confirm:$false
			$sw.Restart()
			do {
				$stillRunning = 
				if (-not $(Get-Process -Name "sysprep" -ErrorAction SilentlyContinue)) { break }
				Start-Sleep -Milliseconds 500
			} while ($sw.Elapsed.TotalSeconds -lt 30)
		} catch { }

		Start-Process -Wait -FilePath "$env:windir\System32\sysprep\sysprep.exe" -ArgumentList "/generalize","/oobe","/quit"

		New-ItemProperty -Force -Path "HKLM:\SYSTEM\Setup\SetupCl\PendingRequest" -Name "SidAccountDomainNew" -PropertyType Binary -Value ([byte[]](0x01,0x04,0x00,0x00,0x00,0x00,0x00,0x05,0x15,0x00,0x00,0x00,0x5E,0x4E,0x90,0xFE,0x4A,0xC1,0x06,0x4C,0x79,0xBF,0xA7,0x0C)) | Out-Null
		# Start-Process -Wait -FilePath reg.exe -ArgumentList 'add','HKLM\SYSTEM\Setup\SetupCl\PendingRequest','/v','SidAccountDomainNew','/t','REG_BINARY','/d','0104000000000005150000005E4E90FE4AC1064C79BFA70C','/f'

		Start-Process -FilePath shutdown.exe -ArgumentList '/r','/f','/t','3' -Wait
	};
);

& {
  [float] $complete = 0;
  [float] $increment = 100 / $scripts.Count;
  foreach( $script in $scripts ) {
    Write-Progress -Activity 'Running scripts to finalize your Windows installation. Do not close this window.' -PercentComplete $complete;
    '*** Will now execute command «{0}».' -f $(
      $str = $script.ToString().Trim() -replace '\s+', ' ';
      $max = 100;
      if( $str.Length -le $max ) {
        $str;
      } else {
        $str.Substring( 0, $max - 1 ) + '…';
      }
    );
    $start = [datetime]::Now;
    & $script;
    '*** Finished executing command after {0:0} ms.' -f [datetime]::Now.Subtract( $start ).TotalMilliseconds;
    "`r`n" * 3;
    $complete += $increment;
  }
} *>&1 | Out-String -Stream >> "C:\Windows\Setup\Scripts\auditUserRunSync.log";
